import React, { useEffect, useState } from 'react';
//import { ActivityIndicator, FlatList, Text, View, Button, TextInput, StyleSheet, SafeAreaView } from 'react-native';

const Search = () => {

  const [isLoading, setLoading] = useState(true)
  const [searchText, setText] = useState('')
  let [data, setData] = useState([])
  const [apiKey, setKey] = useState('&apiKey=e0ba8f96837748dc9473f52b42c3b8a8')
  const [baseSearchURL, setBaseURL] = useState('https://api.spoonacular.com/recipes/complexSearch?number=10&query=')
  var [someLink, setLink] = useState('')


  const getRecipes = async () => {
    setLink(someLink = baseSearchURL + searchText + apiKey)
     try {
      const response = await fetch(someLink)
      const json = await response.json()
      setData(data = json.results)
    } catch (error) {
      console.error(error)
    } finally {
      setLoading(false)
    }
  }

  const onPressSearch = () => {getRecipes()}

  return (
    <div>
        <form onSubmit={this.handleSubmit}>
            <label>
                Name:
                <input type="text" value={this.state.value} onChange={this.handleChange} />
            </label>
                <input type="submit" value="Submit" />
        </form>

        {/* <div>
