import React, { useEffect, useState, Component } from 'react';
//import { ActivityIndicator, FlatList, Text, View, Button, TextInput, StyleSheet, SafeAreaView } from 'react-native';

class Search extends Component {
    constructor(props) {
      super(props);
      this.state = {searchText: ''};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({searchText: event.target.searchText});
    }

    handleSubmit(event) {
      alert('A name was submitted: ' + this.state.searchText);
      event.preventDefault();
    }

    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Name:
            <input type="text" searchText={this.state.searchText} onChange={this.handleChange} />
          </label>
          <input type="submit" searchText="Submit" />
        </form>
      );
    }
  }

export default Search;
