import React, {Component } from 'react';
//import { ActivityIndicator, FlatList, Text, View, Button, TextInput, StyleSheet, SafeAreaView } from 'react-native';

class Search extends Component {
    constructor(props) {
      super(props);
      this.state = {

      isLoading: true,
      searchText: '',
      data: [],
      apiKey: '&apiKey=3f0cc431643b4e64ac67118fd14f646b',
      baseSearchURL: 'https://api.spoonacular.com/recipes/complexSearch?number=10&query=',
      someLink: ''

      }

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    getRecipes = async () => {
        // this.setState({someLink: "this.baseSearchURL + this.searchText + this.apiKey"})
        //  try {
        //   const response = await fetch(this.someLink)
        //   const json = await response.json()
        //   this.setState({data: json.results})
        // } catch (error) {
        //   console.error(error)
        // } finally {
        //   this.setState({isLoading:false})
        // }
        this.setState({someLink: 'hello'})
      }

    handleChange(event) {
      this.setState({searchText: event.target.value});
    }

    handleSubmit(event) {
      this.getRecipes();
      event.preventDefault();
    }

    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Name:
            <input type="search" searchText={this.state.searchText} onChange={this.handleChange} />
          </label>
          <input type="submit" value="Search" />
        </form>
      );
    }
  }

export default Search;
