import React, { useEffect, useState } from 'react';
import { ActivityIndicator, FlatList, Text, View, Button, TextInput, StyleSheet, SafeAreaView } from 'react-native';

const Search = () => {

  const [isLoading, setLoading] = useState(true)
  const [searchText, setText] = useState('')
  let [data, setData] = useState([])
  const [apiKey, setKey] = useState('&apiKey=e0ba8f96837748dc9473f52b42c3b8a8')
  const [baseSearchURL, setBaseURL] = useState('https://api.spoonacular.com/recipes/complexSearch?number=10&query=')
  var [someLink, setLink] = useState('')


  const getRecipes = async () => {
    setLink(someLink = baseSearchURL + searchText + apiKey)
     try {
      const response = await fetch(someLink)
      const json = await response.json()
      setData(data = json.results)
    } catch (error) {
      console.error(error)
    } finally {
      setLoading(false)
    }
  }

  const onPressSearch = () => {getRecipes()}

  return (
    <View style={styles.container}>
        <View>
          <TextInput
            style={styles.input}
            placeholder="Search for a recipe!"
            onChangeText={(searchText) => setText(searchText)}  
            />
        </View>
        <View>
            <Button
            onPress={onPressSearch}
            title= "Search"
            />
        </View>
      {isLoading ? <ActivityIndicator/> : (
        <FlatList
          data={data}
          keyExtractor={({ id }) => id}
          renderItem={({ item }) => (
            <View style={styles.item}>
            <Text style={styles.title}>{item.title}</Text>
            </View>
          )}
        />
      )}
    </View>
  );
};

export default Search;
