import React, { useEffect, useState, Component } from 'react';
//import { ActivityIndicator, FlatList, Text, View, Button, TextInput, StyleSheet, SafeAreaView } from 'react-native';

class Search extends Component {
    constructor(props) {
      super(props);
      this.state = {

      isLoading: true,
      searchText: '',
      data: [],
      apiKey: '&apiKey=e0ba8f96837748dc9473f52b42c3b8a8',
      baseSearchURL: 'https://api.spoonacular.com/recipes/complexSearch?number=10&query=',
      someLink: ''

      }

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    getRecipes = async () => {
        this.setState({someLink: this.baseSearchURL + searchText + apiKey})
         try {
          const response = await fetch(someLink)
          const json = await response.json()
          setData(data = json.results)
        } catch (error) {
          console.error(error)
        } finally {
          setLoading(false)
        }
      }

    handleChange(event) {
      this.setState({searchText: event.target.value});
    }

    handleSubmit(event) {
      alert('A name was submitted: ' + this.state.searchText);
      event.preventDefault();
    }

    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Name:
            <input type="search" searchText={this.state.searchText} onChange={this.handleChange} />
          </label>
          <input type="search" value="Search" />
        </form>
      );
    }
  }

export default Search;
